mapper.select:
{ call MAFCDB.MAFC_SAP_SYNC_COLL(
    #{P_COLLECTOR_NAME, javaType=String, jdbcType=VARCHAR, mode=IN},
    #{P_RESNUM, jdbcType = INTEGER, javaType = java.lang.Integer, mode=OUT},
    #{P_RESULT, javaType=String, jdbcType=VARCHAR, mode=OUT}
)}

{ call MAFCDB.MAFC_SAP_DEL_COLL(
    #{P_COLLECTOR_NAME, javaType=String, jdbcType=VARCHAR, mode=IN},
    #{P_FILE_NAME, javaType=String, jdbcType=VARCHAR, mode=IN},
    #{P_RES, jdbcType = INTEGER, javaType = java.lang.Integer, mode=OUT}
)}

SELECT table_name, column_name, row_num, index_num, update_date, note
FROM mafcdb.mafc_sap_coll_config
WHERE table_name = #{fileName}
